indentation -Wmissing-declarations -Wnonnull -Wnon-virtual-dtor -Wnull-dereference -Wold-style-cast -Woverloaded-virtual -Wpedantic -Wpointer-arith -Wreorder -Wshadow -Wsign-conversion -Wstack-protector -Wstrict-overflow -Wtrampolines -Wunused -Wuseless-cast -Wvla -Wl,-O3 -Wl,--allow-multiple-definition -Wl,--build-id=sha1 -Wl,--discard-locals -Wl,-fno-lto -Wl,-g -Wl,--hash-style=gnu -Wl,--gc-sections -Wl,-melf_i386 -Wl,-z,defs -Wl,-z,relro -Wl,-z,now -Wl,-z,noexecstack -Wl,-rpath=\$$ORIGIN/../lib' -DwxBUILD_CXX_STANDARD=20 -DCMAKE_CXX_STANDARD_REQUIRED=ON -DCMAKE_CXX_EXTENSIONS=OFF -DwxBUILD_COMPATIBILITY=3.1 -DCMAKE_BUILD_TYPE=Debug -DwxBUILD_DEBUG_LEVEL=2 -DwxUSE_MEMORY_TRACING=1 -DwxUSE_GLOBAL_MEMORY_OPERATORS=1 -DwxUSE_DEBUG_CONTEXT=1 -DwxUSE_MEMORY_TRACING=1 -DwxUSE_GLOBAL_MEMORY_OPERATORS=1 -DwxUSE_DEBUG_NEW_ALWAYS=0 -DwxBUILD_DEMOS=OFF -DwxBUILD_INSTALL=ON -DwxBUILD_MONOLITHIC=OFF -DwxBUILD_OPTIMISE=OFF -DwxBUILD_PIC=ON -DwxBUILD_PRECOMP=ON -DwxBUILD_SAMPLES=OFF -DwxBUILD_SHARED=ON -DwxUSE_REPRODUCIBLE_BUILD=OFF -DwxBUILD_TESTS=OFF -DwxBUILD_TOOLKIT=gtk3 -DwxBUILD_USE_STATIC_RUNTIME=OFF -DwxUSE_EXCEPTIONS=1 -DwxUSE_WEBKIT=OFF -DwxUSE_WEBVIEW=OFF -DwxUSE_SECRETSTORE=OFF -DwxUSE_MEDIACTRL=OFF -DCMAKE_INSTALL_PREFIX=$HOME/Dev/Binaries/wxWidgetsx86Debug/
