# To write the Output into a Log File: 1>~/Dev/wxWidgetsx86DebugLog.log 2>&1
ninja --verbose -j$(nproc) -l$(nproc) -k1 1>/dev/null 2>&1
ninja --verbose -j$(nproc) install
ninja --verbose -j1 clean
rm --interactive=never --force --verbose $HOME/Dev/Builds/wxWidgetsx86/CMakeCache.txt
